import{_ as i,C as r,o,c as l,j as a,a as t,E as d}from"./chunks/framework.amPE58To.js";const g=JSON.parse('{"title":"Glossary","description":"","frontmatter":{"title":"Glossary","sidebar":false,"pref":false,"next":false,"aside":false,"glossary":[{"title":"Artificial Intelligence (AI)","text":"Artificial Intelligence (AI) in its broadest sense, is intelligence exhibited by machines. It encompasses various techniques and systems that enable tasks such as problem-solving, natural language processing, and decision-making to be performed autonomously.\\n"},{"title":"Graphics Processing Unit (GPU)","text":"A GPU (Graphics Processing Unit) is a specialized processor designed to handle computationally intensive tasks by performing parallel operations across thousands of cores. In research and data science, GPUs accelerate tasks like deep learning, simulations, and large-scale data analysis, significantly reducing processing time compared to traditional CPUs.\\n"},{"title":"Helseundersøkelsen i Trøndelag (HUNT)","text":"The Trøndelag Health Study (The HUNT Study) is one of the largest health studies ever performed. It is a unique database of questionnaire data, clinical measurements and samples from a county’s inhabitants since 1984.\\n"},{"title":"Machine Learning (ML)","text":"Machine learning (ML) is a subset of artificial intelligence that enables computers to learn and improve from experience without being explicitly programmed. It uses algorithms to analyze data, identify patterns, and make predictions or decisions based on that data.\\n"},{"title":"Sensitive AI","text":"Sensitive AI is all about using sensitive data when training of AI/ML models.\\n"},{"title":"Sensitive Data","text":"Sensitive data is information that must be protected from unauthorized access to ensure privacy, security, and compliance with regulations. It includes personal details like social security numbers, financial information, health records, and any data that, if exposed, could harm individuals or organizations.\\n"},{"title":"Health data access body (HDAB)","text":"The HDAB enables data users such as researchers and policymakers to apply for access to health datasets. This supports research and innovation, education and training, policymaking, health service management, and the preparation of national statistics.\\n"},{"title":"Data permit","text":"\\"data permit\\" means an administrative decision issued to a health data user by a health data access body to process certain electronic health data specified in the data permit for specific secondary use purposes.\\n"}]},"headers":[],"relativePath":"en/about/glossary.md","filePath":"en/about/glossary.md"}'),c={name:"en/about/glossary.md"};function m(s,e,u,h,p,y){const n=r("GlossaryPanels");return o(),l("div",null,[e[0]||(e[0]=a("h1",{id:"these-days-there-s-an-acronym-for-everything",tabindex:"-1"},[t("These days, there’s an acronym for everything "),a("a",{class:"header-anchor",href:"#these-days-there-s-an-acronym-for-everything","aria-label":'Permalink to "These days, there’s an acronym for everything"'},"​")],-1)),e[1]||(e[1]=a("br",null,null,-1)),e[2]||(e[2]=t(" Here you can look up the meaning of those tricky terms. ",-1)),e[3]||(e[3]=a("br",null,null,-1)),e[4]||(e[4]=a("br",null,null,-1)),d(n,{items:s.$frontmatter.glossary},null,8,["items"])])}const b=i(c,[["render",m]]);export{g as __pageData,b as default};
